    return (
        <>
            <h3 style={{ textAlign: 'center' }}>List of Products</h3>

            <div className='row'>
                <div className='col-md-4'>
                    <div >Categories</div>
                    {Category.map((e, i) => {
                        return (<div key={i}>
                            <input name='Categories' type='radio' /> <span>{e.name}</span>
                        </div>)
                    })}

                    <div>Brands</div>
                    {Brand.map((e, i) => {
                        return (<div key={i}>
                            <input name='Brands' type='radio' /> <span>{e.name}</span>
                        </div>)
                    })}
                </div>
                <div className='col-md-8'>
                    <div className='row' >
                        {products.map((e, i) => {
                            return (
                                <Card className='col-md-3'>
                                    <Card.Header style={{textAlign: 'center'}}>
                                        <img style={{width: '45%', height: '100px'}} src={'images/'+e.image} />
                                    </Card.Header>
                                    <Card.Body>
                                        <Card.Title style={{textAlign: 'center'}} >{e.title}</Card.Title>
                                        <Card.Text>Brand: </Card.Text> {e.brand?Brand.name:""}


                                    </Card.Body>
                                </Card>
                            )
                        })}
                    </div>

                </div>
            </div>

        </>
    )
}
