import axios from 'axios';
import React, { useState, useEffect } from 'react'

export default function ListMovie() {
    const [movie, setMovie] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [Genre, setGenre] = useState([]);
    const [Producer, setProducer] = useState([]);


    const fetchProducer = async () => {
        const response = await axios.get('http://localhost:9999/producers');
        const data = response.data;
        setProducer(data);
        console.log(data);
    }

    const fetchGenre = async () => {
        const response = await axios.get('http://localhost:9999/movies');
        const data = response.data;
        let genreList = [];
        console.log(data);
        data.map((item) => {
            item.genres.map((genre) => {
                if (!genreList.includes(genre)) {
                    genreList.push(genre);
                }
            })
        })
        setGenre(genreList);
        console.log(genreList);
    }

    useEffect(() => {
        fetchGenre();
        fetchProducer();
    }, []);

    return (
        <>
            <h1 className='text-center'>React Application</h1>
            <hr></hr>
            <div>
                <div className='row'>
                    <div className='col-md-12 d-flex justify-content-center'>
                        {Genre ? (Genre.map((item, i) => {
                            return (
                                <a key={i} href='#' className='m-2'>{item}</a>
                            )
                        })) : ("")}
                    </div>
                </div>
            </div>
            <hr></hr>
            <div className='row'>
                <div className='col-md-4'>
                    <h3>Producers</h3>
                    <ul>
                        {Producer ? (Producer.map((item, i) => {
                            return (
                                <li> <a key={i} href='#' className='m-2'>{item.name}</a> </li>
                            )
                        })) : ("")}
                    </ul>
                </div>
                <div className='col-md-8'>
                    <h3 className='text-center'>List Of Movies</h3>
                    <span><a href='#' className='m-2'>Show More Options</a></span>
                </div>
            </div>

        </>
    )
}
